var gulp = require('swiped/node_modules/gulp');
var uglify = require('swiped/node_modules/gulp-uglify');
var jshint = require('swiped/node_modules/gulp-jshint');
var rename = require('swiped/node_modules/gulp-rename');

gulp.task('compress', function() {
    return gulp.src('dist/swiped.js')
        .pipe(uglify())
        .pipe(rename('swiped.min.js'))
        .pipe(gulp.dest('dist'));
});

gulp.task('lint', function() {
    return gulp.src('dist/swiped.js')
        .pipe(jshint())
        .pipe(jshint.reporter('default'));
});

gulp.task('default', ['lint', 'compress']);